require { type dns_port_t; type systemd_resolved_t; class tcp_socket name_bind; class capability { net_bind_service net_raw }; class udp_socket name_bind; }
#============= systemd_resolved_t ============== allow systemd_resolved_t dns_port_t:tcp_socket name_bind; allow systemd_resolved_t dns_port_t:udp_socket name_bind; allow systemd_resolved_t self:capability { net_bind_service net_raw };
~]# cat fixsystemdresolveddbus.te
module fixsystemdresolveddbus 1.0;
require { type kernel_t; type sssd_t; type gssd_t; type systemd_resolved_t; type system_dbusd_t; type mount_t; class dbus send_msg; }
#============= gssd_t ============== allow gssd_t system_dbusd_t:dbus send_msg; allow gssd_t systemd_resolved_t:dbus send_msg;
#============= sssd_t ============== allow sssd_t systemd_resolved_t:dbus send_msg;
#============= systemd_resolved_t ============== allow systemd_resolved_t gssd_t:dbus send_msg; allow systemd_resolved_t kernel_t:dbus send_msg; allow systemd_resolved_t mount_t:dbus send_msg; allow systemd_resolved_t sssd_t:dbus send_msg;
